use crate::types::TransactionNative;

use std::str::FromStr;
use wasm_bindgen::prelude::wasm_bindgen;

/// Webassembly Representation of an Aleo transaction
///
/// This object is created when generating an on-chain function deployment or execution and is the
/// object that should be submitted to the Aleo Network in order to deploy or execute a function.
#[wasm_bindgen]
#[derive(Clone, Debug, PartialEq, Eq)]
pub struct Transaction(TransactionNative);

#[wasm_bindgen]
impl Transaction {
    /// Create a transaction from a string
    #[wasm_bindgen(js_name = fromString)]
    pub fn from_string(transaction: &str) -> Result<Transaction, String> {
        Transaction::from_str(transaction)
    }

    /// Get the transaction as a string. If you want to submit this transaction to the Aleo Network
    /// this function will create the string that should be submitted in the `POST` data.
    #[wasm_bindgen(js_name = toString)]
    #[allow(clippy::inherent_to_string)]
    pub fn to_string(&self) -> String {
        self.0.to_string()
    }

    /// Get the id of the transaction. This is the merkle root of the transaction's inclusion proof.
    ///
    /// This value can be used to query the status of the transaction on the Aleo Network to see
    /// if it was successful. If successful, the transaction will be included in a block and this
    /// value can be used to lookup the transaction data on-chain.
    #[wasm_bindgen(js_name = transactionId)]
    pub fn transaction_id(&self) -> String {
        self.0.id().to_string()
    }

    /// Get the type of the transaction (will return "deploy" or "execute")
    #[wasm_bindgen(js_name = transactionType)]
    pub fn transaction_type(&self) -> String {
        match &self.0 {
            TransactionNative::Deploy(..) => "deploy".to_string(),
            TransactionNative::Execute(..) => "execute".to_string(),
            TransactionNative::Fee(..) => "fee".to_string(),
        }
    }
}

impl From<Transaction> for TransactionNative {
    fn from(transaction: Transaction) -> Self {
        transaction.0
    }
}

impl From<TransactionNative> for Transaction {
    fn from(transaction: TransactionNative) -> Self {
        Self(transaction)
    }
}

impl FromStr for Transaction {
    type Err = String;

    fn from_str(s: &str) -> Result<Self, Self::Err> {
        Ok(Self(TransactionNative::from_str(s).map_err(|e| e.to_string())?))
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use wasm_bindgen_test::*;
    const TRANSACTION_STRING: &str = "{\"type\":\"execute\",\"id\":\"at1pkw4ms8yuw29k8lfqdqkcdaffd6hngnzkw3j8f8j0aht0egkz5fq4h652c\",\"execution\":{\"transitions\":[{\"id\":\"as1w46uteuwlm85yp85af0xsd52pt4qa04m2nm20ux6zh78u5d4ecys6v85ch\",\"program\":\"credits.aleo\",\"function\":\"mint\",\"inputs\":[{\"type\":\"public\",\"id\":\"5040908569006131213612149758844338587983081600819749361338731441075240131558field\",\"value\":\"aleo1q6qstg8q8shwqf5m6q5fcenuwsdqsvp4hhsgfnx5chzjm3secyzqt9mxm8\"},{\"type\":\"public\",\"id\":\"1276789444601847309812664117152425665973038141628909950908919191663021093021field\",\"value\":\"1u64\"}],\"outputs\":[{\"type\":\"record\",\"id\":\"95928634596052979246019218801908083236653595200887025551448057926055637040field\",\"checksum\":\"7583379478720147365928563614237196759288927925463919661327931764780242134350field\",\"value\":\"record1qyqsqw5vumkswscee2ht4yju9el5g02uwv7204sx8nf96ln4mjugmasyqyxx66trwfhkxun9v35hguerqqpqzqpken3n6ghr0mm509pn7sgersrrnxfvcp5zsctd9cr03ay2yncap2khg34cktpumr8mnv7sqa4zjxu9ww78u69nfqxh9mwxxqvv6ssqsepzx3u\"}],\"proof\":\"\",\"tpk\":\"6632149043115422828236144456828746891849538829103670866843594215187740194210group\",\"tcm\":\"4818412099071103605161859576211275541485358629762154526095011715785635126388field\"}],\"global_state_root\":\"ar1jexd2yp8k5lal4rn7khtf0ejgzqcq7rada0chywes7q3hvmcxqgq0u4930\"}}";
    const TRANSACTION_ID: &str = "at1pkw4ms8yuw29k8lfqdqkcdaffd6hngnzkw3j8f8j0aht0egkz5fq4h652c";

    
    fn test_transaction_string_constructor_and_accessor_methods() {
        let transaction = Transaction::from_string(TRANSACTION_STRING).unwrap();
        let transaction_id = transaction.transaction_id();
        let transaction_type = transaction.transaction_type();
        let recovered_string = transaction.to_string();
        assert_eq!(transaction_id, TRANSACTION_ID);
        assert_eq!(transaction_type, "execute");
        assert_eq!(recovered_string, TRANSACTION_STRING);

        // Test to and from round trip
        let transaction_native = TransactionNative::from_str(TRANSACTION_STRING).unwrap();
        let transaction_deconstruction = TransactionNative::from(transaction.clone());
        assert_eq!(transaction_native, transaction_deconstruction);
        let transaction_from_native = Transaction::from(transaction_native);
        assert_eq!(transaction, transaction_from_native);
    }
}
